function openWindow(t){var e="";switch(t.path[0].dataset.network){case"facebook":e="http://www.facebook.com/sharer.php?u="+window.location.href+"&title="+document.title;break;case"twitter":e="https://twitter.com/intent/tweet?url="+window.location.href+"&text="+document.title+"&via=sagacify";break;case"pinterest":e="http://pinterest.com/pin/create/button/?url="+window.location.href+"&title="+document.title;break;case"googleplus":e="http://plus.google.com/share?url=URL"+window.location.href+"&title="+document.title;break;default:console.log(t)}return window.open(e,"sharewindow","height=450,width=550, top="+(window.innerHeight/2-275)+", left="+(window.innerWidth/2-225)+", toolbar=0, location=0, menubar=0, directories=0, scrollbars=0"),!1}var social=document.querySelectorAll(".social--button");null!=social&&Array.prototype.forEach.call(social,function(t,e){t.addEventListener("click",function(t){t.preventDefault,openWindow(t)})});